// Options:
// key0 - 0x 01 00 00 00   00 00 00 00
// key1 - 0x 00 00 00 01   00 00 00 00
// key2 - 0x 00 00 00 00   00 00 00 01
// key3 - 0x 00 00 00 00   01 00 00 00

// Key2 = no byte swap
// Key3 = byte swap
// Key4 = big + swpa32

uint32_t ticks() {
    return xTaskGetTickCount();
}

void delay(uint32_t duration) {
    vTaskDelay((duration + portTICK_PERIOD_MS - 1) / portTICK_PERIOD_MS);
}

void panic(char *message, int code) {
    printf("[PANIC] %s (code=%d; %s)\n\n", message, code,
      esp_err_to_name(code));
    while(1) { delay(1000); }
}

void reverseBytes(uint8_t *data, size_t length) {
    for (int i = 0; i < length / 2; i++) {
        uint8_t tmp = data[i];
        data[i] = data[length - 1 - i];
        data[length - 1 - i] = tmp;
    }
}

void reverseWords(uint8_t *data, size_t words) {
    for (int i = 0; i < words; i += 4) {

        uint8_t tmp = data[i + 0];
        data[i + 0] = data[i + 3];
        data[i + 3] = tmp;

        tmp = data[i + 1];
        data[i + 1] = data[i + 2];
        data[i + 2] = tmp;
    }
}

void copy_value(uint8_t *dst, uint8_t *src, size_t length, bool reverseBytes, bool reverseWords) {
    for (int i = 0; i < length; i++) {
        dst[i] = src[i];
    }

    if (reverseBytes) {
        for (int i = 0; i < length / 2; i++) {
            uint8_t tmp = dst[i];
            dst[i] = dst[length - 1 - i];
            dst[length - 1 - i] = tmp;
        }
    }

    if (reverseWords) {
        for (int i = 0; i < length; i += 4) {
            uint8_t tmp = dst[i + 0];
            dst[i + 0] = dst[i + 3];
            dst[i + 3] = tmp;

            tmp = dst[i + 1];
            dst[i + 1] = dst[i + 2];
            dst[i + 2] = tmp;
        }
    }
}

static int copy_mpi(uint32_t keySize, mbedtls_mpi* value, uint32_t *dst, bool reverseBytes, bool reverseWords) {
    uint8_t tmp[keySize / 8];
    int ret = mbedtls_mpi_write_binary(value, tmp, keySize / 8);
    if (ret) { panic("failed to write_binary", ret); }
    copy_value((uint8_t*)dst, tmp, keySize / 8, reverseBytes, reverseWords);
    return ret;
}

//https://github.com/espressif/esp_secure_cert_mgr/issues/8
static void compute_rinv_mprime(uint32_t keySize, mbedtls_mpi* N, mbedtls_mpi* rinv, uint32_t* mprime) {
    // python equivalent code:
    //
    //    key_size = private_key.key_size # in bits
    //
    //    # calculate rinv == Rb
    //    rr = 1 << (key_size * 2)
    //    rinv = rr % pub_numbers.n # RSA r inverse operand
    //
    //    # calculate MPrime
    //    a = rsa._modinv(M, 1 << 32)
    //    mprime = (a * -1) & 0xFFFFFFFF # RSA M prime operand

    // rr = 1 << (key_size * 2) # in bits
    mbedtls_mpi rr;
    mbedtls_mpi_init(&rr);
    mbedtls_mpi_lset(&rr, 1);
//    mbedtls_mpi_shift_l(&rr, sizeof(pd_4096_bit_t) * 8 * 2);
    mbedtls_mpi_shift_l(&rr, keySize * 2); // RicMoo!

    // rinv = rr % rsa.N
    mbedtls_mpi_mod_mpi(rinv, &rr, N);

    // ls32 = 1 << 32
    mbedtls_mpi ls32;
    mbedtls_mpi_init(&ls32);
    mbedtls_mpi_lset(&ls32, 1);
    mbedtls_mpi_shift_l(&ls32, 32);

    // a = rsa._modinv(N, 1 << 32)
    mbedtls_mpi a;
    mbedtls_mpi_init(&a);
    mbedtls_mpi_inv_mod(&a, N, &ls32);

    // a32 = a
    uint32_t a32 = 0;
    mbedtls_mpi_write_binary_le(&a, (uint8_t*) &a32, sizeof(uint32_t));

    // mprime
    *mprime = ((int32_t) a32 * -1) & 0xFFFFFFFF;
}

static int getKeyBlock(int key) {
    switch(key) {
        case 0: return EFUSE_BLK_KEY0;
        case 1: return EFUSE_BLK_KEY1;
        case 2: return EFUSE_BLK_KEY2;
        case 3: return EFUSE_BLK_KEY3;
        case 4: return EFUSE_BLK_KEY4;
    }
    panic("unknown key block", key);
    return -1;
}

static int getHmacKey(int key) {
    switch(key) {
        case 0: return HMAC_KEY0;
        case 1: return HMAC_KEY1;
        case 2: return HMAC_KEY2;
        case 3: return HMAC_KEY3;
        case 4: return HMAC_KEY4;
    }
    panic("unknown hmac key", key);
    return -1;
}

