void dumpMpi(char *header, mbedtls_mpi* value) {
    char str[2048];
    size_t olen = 0;

    int ret = mbedtls_mpi_write_string(value, 16, str, sizeof(str), &olen);

    if (ret == 0) {
        printf("%s%s (length=%d bits)\n", header, str, (olen - 1) * 4);
    } else {
        printf("%s[FAILED]\n", header);
    }
}

void dumpBuffer(char *header, uint8_t *buffer, size_t length) {
    static char *hex = "0123456789abcdef";

    printf("%s", header);
    for (uint32_t i = 0; i < length; i++) {
        printf("%c%c", hex[buffer[i] >> 4], hex[buffer[i] & 0x0f]);
    }
    printf("  (%d bytes)\n", length);
}

void dumpArray(char *header, uint8_t *buffer, size_t length) {
    static char *hex = "0123456789abcdef";

    printf("%s = {", header);
    for (uint32_t i = 0; i < length; i++) {
        printf("0x%c%c, ", hex[buffer[i] >> 4], hex[buffer[i] & 0x0f]);
    }
    printf("  }\n");
}

